import {authConstants} from "../actions/constants";

const initState = {
    name: 'Israel'
};


export default (state = initState, action) => {

    const dispatch = useDispatch();
    console.log(action);

    switch (action.type) {
        case authConstants.LOGIN_REQUEST:
            state = {
                ...state,
                ...action.payload
            };
            break;
    }

    return state;
}
